           private void table_update() {
        int CC;
        try {
            
          Class.forName("com.mysql.jdbc.Driver");
          String url = "jdbc:mysql://localhost:3306/stockManagement";
          String username = "root";
          String pass = "";
          Connection conn = DriverManager.getConnection(url, username, pass);
            PreparedStatement inser = conn.prepareStatement("SELECT * FROM user");
            ResultSet Rs = inser.executeQuery();
            ResultSetMetaData RSMD = Rs.getMetaData();
            CC = RSMD.getColumnCount();
            DefaultTableModel DFT = (DefaultTableModel) jTable3.getModel();
            DFT.setRowCount(0);
            while (Rs.next()) {
                
                Vector v2 = new Vector();
                
                for (int ii = 1; ii <= CC; ii++) {
                    v2.add(Rs.getString("userid"));
                    v2.add(Rs.getString("username"));
                    v2.add(Rs.getString("email"));
                    v2.add(Rs.getString("password"));
                    v2.add(Rs.getString("role"));
                    
                }
                DFT.addRow(v2);
            }
        } catch (Exception e) {
        }
    }
