    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        DefaultTableModel model = (DefaultTableModel) jTable3.getModel();
            int selectedIndex = jTable3.getSelectedRow();
            try {   
                
            int id = Integer.parseInt(model.getValueAt(selectedIndex, 0).toString());
            Class.forName("com.mysql.jdbc.Driver");
            con1 = DriverManager.getConnection("jdbc:mysql://localhost/stockManagement","root","");
                        if("".equals(Uname.getText())){  
                JOptionPane.showMessageDialog(new  JFrame(), " Name is required ", " Error has occure ", JOptionPane.ERROR_MESSAGE);
            }else if("".equals( Uemail.getText())){
                JOptionPane.showMessageDialog(new  JFrame(), "Email is required ", " Error has occure ", JOptionPane.ERROR_MESSAGE);
            }else if("".equals( Upwd.getText())){
                JOptionPane.showMessageDialog(new  JFrame(), "Password is required ", " Error has occure ", JOptionPane.ERROR_MESSAGE);
            }else if("Choose Role".equals( (String)Role.getSelectedItem())){
                JOptionPane.showMessageDialog(new  JFrame(), "Select a role ", " Error has occure ", JOptionPane.ERROR_MESSAGE);
            }else{
           
               String name = Uname.getText();
               String email = Uemail.getText();
               String pwd = Upwd.getText();
               String role = (String)Role.getSelectedItem();
            insert = con1.prepareStatement("update user set username= ?,email= ?,password= ?, role = ? where userid= ?");
             System.out.println("out");
            insert.setString(1,name);
            insert.setString(2,email);
            insert.setString(3,pwd);
            insert.setString(4,role );
            insert.setInt(5,id);
            insert.executeUpdate();
            JOptionPane.showMessageDialog(this, "User Updated");
            Uname.setText("");
            Uemail.setText("");
            Upwd.setText("");
            table_update();
            }
            
        } catch (ClassNotFoundException ex) {
           
        } catch (SQLException ex) {

        }
    }//GEN-LAST:event_jButton3ActionPerformed

