    private void signbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_signbtnActionPerformed
        // TODO add your handling code here:
        String name, email,pwd,query;
        try{
            Class.forName("com.mysql.cj.jdbc.Driver");
            String url = "jdbc:mysql://localhost:3306/stockManagement";
            String username = "root";
            String pass = "";
            Connection conn = DriverManager.getConnection(url, username, pass);
            Statement st = conn.createStatement();
            if("".equals(Uname.getText())){  
                JOptionPane.showMessageDialog(new  JFrame(), " Name is required ", " Error has occure ", JOptionPane.ERROR_MESSAGE);
            }else if("".equals( Uemail.getText())){
                JOptionPane.showMessageDialog(new  JFrame(), "Email is required ", " Error has occure ", JOptionPane.ERROR_MESSAGE);
            }else if("".equals( Upwd.getText())){
                JOptionPane.showMessageDialog(new  JFrame(), "Password is required ", " Error has occure ", JOptionPane.ERROR_MESSAGE);
            }else if("Choose Role".equals( (String)Role.getSelectedItem())){
                JOptionPane.showMessageDialog(new  JFrame(), "Select a role ", " Error has occure ", JOptionPane.ERROR_MESSAGE);
            }else
            {
                name = Uname.getText();
                email = Uemail.getText();
                pwd = Upwd.getText();
                String role =(String) Role.getSelectedItem();
                query = "INSERT INTO user (username,email,password,role)"+"VALUES('"+name+"','"+email+"','"+pwd+"','"+role+"')";
                st.execute(query);
                showMessageDialog(null, "User Added Successfully");
                Uname.setText("");
                Uemail.setText("");
                Upwd.setText("");
                table_update();
            }
        }catch(Exception e){
                 JOptionPane.showMessageDialog(new  JFrame(), e.getMessage(), " Error has occure ", JOptionPane.ERROR_MESSAGE);
            System.out.println("Error!"+e.getMessage());

        }
    }//GEN-LAST:event_signbtnActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        DefaultTableModel model = (DefaultTableModel) jTable3.getModel();
        int selectedIndex = jTable3.getSelectedRow();
        try {

            int id = Integer.parseInt(model.getValueAt(selectedIndex, 0).toString());
            int dialogResult = JOptionPane.showConfirmDialog (null, "Do you want to Delete the user","Warning",JOptionPane.YES_NO_OPTION);
            if(dialogResult == JOptionPane.YES_OPTION){

                Class.forName("com.mysql.jdbc.Driver");
                con1 = DriverManager.getConnection("jdbc:mysql://localhost/stockManagement","root","");
                insert = con1.prepareStatement("delete from user where userid = ?");

                insert.setInt(1,id);
                insert.executeUpdate();
                JOptionPane.showMessageDialog(this, "user Deleted Successfully");
                Uname.setText("");
                Uemail.setText("");
                Upwd.setText("");
                table_update();
            }
        } catch (ClassNotFoundException ex) {
        } catch (SQLException ex) {
        }
    }//GEN-LAST:event_jButton2ActionPerformed

